"use strict";

var userTable = "CREATE TABLE IF NOT EXISTS users (\n                    id SERIAL PRIMARY KEY,\n                    email VARCHAR UNIQUE NOT NULL,\n                    first_name TEXT NOT NULL,\n                    last_name TEXT NOT NULL,\n                    password TEXT NOT NULL,\n                    is_admin BOOLEAN DEFAULT false,\n                    created_on TIMESTAMP WITH TIME ZONE DEFAULT NOW()\n                    );";
var busTable = "CREATE TABLE IF NOT EXISTS buses(\n                    id SERIAL PRIMARY KEY,\n                    plate_number TEXT UNIQUE NOT NULL,\n                    manufacturer TEXT NOT NULL,\n                    model TEXT NOT NULL,\n                    year TEXT NOT NULL,\n                    capacity INT NOT NULL\n                    );";
var tripTable = "CREATE TABLE IF NOT EXISTS trips (\n                    id SERIAL PRIMARY KEY,\n                    bus_id INT NOT NULL,\n                    origin TEXT NOT NULL,\n                    destination TEXT NOT NULL,\n                    trip_date TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n                    fare NUMERIC(10, 2) NOT NULL,\n                    status TEXT DEFAULT 'active',\n                    booking_status INT DEFAULT 0,\n                    passengers TEXT DEFAULT 'none',\n                    FOREIGN KEY (bus_id) REFERENCES buses (id)\n                    );";
var bookingTable = "CREATE TABLE IF NOT EXISTS bookings(\n                    id SERIAL NOT NULL,\n                    trip_id INT NOT NULL,\n                    user_id INT NOT NULL,\n                    bus_id INT NOT NULL,\n                    origin TEXT NOT NULL,\n                    destination TEXT NOT NULL,\n                    trip_date TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n                    seat_number INT DEFAULT 0,\n                    created_on TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n                    PRIMARY KEY(trip_id, user_id),\n                    FOREIGN KEY (trip_id) REFERENCES trips (id),\n                    FOREIGN KEY (user_id) REFERENCES users (id)\n                    );";
var deletedTable = "CREATE TABLE IF NOT EXISTS deletedbookings (\n                        id SERIAL PRIMARY KEY,\n                        booking_id INT NOT NULL,\n                        trip_id INT NOT NULL,\n                        user_id INT NOT NULL,\n                        bus_id INT NOT NULL,\n                        trip_date TEXT NOT NULL,\n                        origin TEXT NOT NULL,\n                        destination TEXT NOT NULL,\n                        seat_number INT NOT NULL,\n                        deleted_on TIMESTAMP WITH TIME ZONE DEFAULT NOW()\n                        );";
var allTables = "".concat(userTable).concat(busTable).concat(tripTable).concat(bookingTable).concat(deletedTable);
module.exports = allTables;